<script lang="ts" setup>
import {
  Card,
  CardContent,
  CardFooter,
} from '@/components/ui/card'
import { Avatar } from '@/components/ui/avatar'

const props = defineProps({
  message: { type: String, required: true },
  created_at: { type: Date, required: false },
  role: { type: String, required: true },
  success: { type: Boolean, default: true, requided: true },
})

const classPosition = computed(() => {
  return props.role === 'USER' ? 'box-message-right' : 'box-message-left'
})

const classDanger = computed(() => {
  return !props.success ? 'box-message-danger' : ''
})

const dateFormat = computed(() => {
  if (!props.created_at) {
    return '';
  }
  return new Date(props.created_at).toLocaleString('en-US', {
    hour: '2-digit',
    minute: '2-digit',
  })
})

</script>

